    // Multiply input reflectance values by 2.5 to increase brighness and by 255 to return the band values clamped to [0, 255] unsigned 8 bit range.
    true_color_8bit: [
      2.5 * sample.B04 * 255,
      2.5 * sample.B03 * 255,
